class Canvas{constructor(t){this.id=t,this.objectsToListen=[],this.running=!1;try{if(this.element=document.getElementById(this.id),!this.element)throw new Error('Canvas with id "'+t+'" not found!');canvases.push(this),this.ctxt=this.element.getContext("2d"),this.setFramerate(60),this.setSize(window.innerWidth,window.innerHeight),this.setBackgroundColor("rgba(0,0,0,1)"),this.setForegroundColor("rgba(255,255,255,1)")}catch(t){console.error(t)}}setSize(t,e){this.width=t,this.height=e,this.element.width=t,this.element.height=e}setListenerObjects(t){this.objectsToListen=[];for(let e=0;e<t.length;e++)this.objectsToListen.push(t[e])}resetListenerObjects(){this.objectsToListen=[]}start(){this.running=!0;let t=Math.floor(1e3/this.framerate);this.interval=setInterval(()=>{this.draw()},t)}end(){this.running=!1,clearInterval(this.timer)}draw(){this.ctxt.fillStyle=this.bColor,this.ctxt.fillRect(0,0,this.width,this.height);for(let t=0;t<this.objectsToListen.length;t++)this.ctxt.fillStyle=this.objectsToListen[t].color?this.objectsToListen[t].color:this.fColor,this.ctxt.beginPath(),this.ctxt.arc(this.objectsToListen[t].x,this.objectsToListen[t].y,this.objectsToListen[t].radius,0,2*Math.PI),this.ctxt.fill()}setFramerate(t){this.running&&this.end(),this.framerate=t}setBackgroundColor(t){this.bColor=t,this.ctxt.fillStyle=this.bColor}setForegroundColor(t){this.fColor=t,this.ctxt.strokeStyle=this.fColor}}